mapper.select:
/* kr.apfs.local.announce.dao.impl.AnnounceDao.selectAnnouncePageList */
SELECT announc_id ,year ,seq ,contents ,act_month ,mng_stadt ,mng_enddt ,to_char(to_date(mng_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(mng_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as mng_dt ,aply_stadt ,aply_enddt ,to_char(to_date(aply_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(aply_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as aply_dt ,eval_stadt ,eval_enddt ,to_char(to_date(eval_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(eval_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as eval_dt ,open_yn ,to_char(to_date(rel_dt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as rel_dt ,reg_user_id ,reg_dt FROM t_announce a WHERE del_yn = 'N'
AND year = #{year}
AND open_yn = #{openYn}

/* kr.apfs.local.announce.dao.impl.AnnounceDao.selectAnnounceList */
SELECT ROWNUM AS RNUM,
     				announc_id
			,year
			,seq
			,contents
			,act_month
			,mng_stadt
			,mng_enddt
			,aply_stadt
			,aply_enddt
			,eval_stadt
			,eval_enddt
			,open_yn
			,rel_dt
			,reg_user_id
			,reg_dt
FROM t_announce
WHERE 1 = 1

		   AND announc_id = #{announcId}


		   AND year = #{year}


		   AND seq = #{seq}


		   AND contents = #{contents}


		   AND act_month = #{actMonth}


		   AND mng_stadt = #{mngStaDt}


		   AND mng_enddt = #{mngEndDt}


		   AND aply_stadt = #{aplyStaDt}


		   AND aply_enddt = #{aplyEndDt}


		   AND eval_stadt = #{evalStaDt}


		   AND eval_enddt = #{evalEndDt}


		   AND open_yn = #{openYn}


		   AND rel_dt = #{relDt}


		   AND reg_user_id = #{*userId}


		   AND reg_dt = #{regDt}


		   AND upd_user_id = #{*userId}


		   AND upd_dt = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')

/* kr.apfs.local.announce.dao.impl.AnnounceDao.selectAnnounceInfo */ SELECT announc_id announcId ,year year ,seq seq ,contents contents ,act_month actMonth ,mng_stadt mngStadt ,mng_enddt mngEnddt ,mng_dt mngDt ,aply_stadt aplyStadt ,aply_enddt aplyEnddt ,aply_dt aplyDt ,eval_stadt evalStadt ,eval_enddt evalEnddt ,eval_dt evalDt ,open_yn openYn ,rel_dt relDt
,(case when mng_enddt < to_char(sysdate, 'YYYYMMDDHH24MISS') then 'E' when (select max(aply_compl_dt) from t_apply where announc_id = a.announc_id and user_id = #{*userId}) IS NOT NULL then 'C' when aply_stadt > to_char(sysdate, 'YYYYMMDDHH24MISS') then 'B' when aply_stadt <= to_char(sysdate, 'YYYYMMDDHH24MISS') and aply_enddt >= to_char(sysdate, 'YYYYMMDDHH24MISS') then 'A' when aply_enddt < to_char(sysdate, 'YYYYMMDDHH24MISS') and rel_dt > to_char(sysdate, 'YYYYMMDDHH24MISS') then 'AE' when rel_dt <= to_char(sysdate, 'YYYYMMDDHH24MISS') and (mng_stadt <= to_char(sysdate, 'YYYYMMDDHH24MISS') and mng_enddt >= to_char(sysdate, 'YYYYMMDDHH24MISS')) then 'M' else 'E' end) as state
,age ,reg_user_id regUserId ,reg_dt regDt ,nvl((select count(x.aply_id) from t_apply x inner join t_orientation y on x.aply_id = y.aply_id where x.user_id = #{*userId} and y.agree_yn = 'Y'),0) as preSubmitCnt FROM ( SELECT announc_id announcId ,year year ,seq seq ,contents contents ,act_month actMonth ,mng_stadt mngStadt ,mng_enddt mngEnddt ,aply_stadt aplyStadt ,aply_enddt aplyEnddt ,eval_stadt evalStadt ,eval_enddt evalEnddt ,open_yn openYn ,reg_user_id regUserId ,reg_dt regDt ,to_char(to_date(mng_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(mng_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as mngDt ,to_char(to_date(aply_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(aply_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as aplyDt ,to_char(to_date(eval_stadt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI')+'~'+to_char(to_date(eval_enddt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as evalDt ,to_char(to_date(rel_dt,'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI') as relDt ,(select floor(months_between(to_date(to_char(sysdate,'yyyy')+'0101'), to_date(substr(y.birth,0,4)+'0101','YYYYMMDD'))/12)-1 from t_user y where user_id = #{*userId}) as age
FROM t_announce x WHERE del_yn = 'N' and open_yn = 'Y' and year = #{year}
and ( mng_stadt <= to_char(sysdate, 'YYYYMMDDHH24MISS') and mng_enddt >= to_char(sysdate, 'YYYYMMDDHH24MISS') )
) a ORDER BY seq desc

/* kr.apfs.local.announce.dao.impl.AnnounceDao.selectAnnounceExist */
SELECT
		  count(*)
	FROM t_announce
	WHERE	announc_id = #{announcId}

mapper.insert:
/* kr.apfs.local.announce.dao.impl.AnnounceDao.insertAnnounce */
select nvl(max(announc_id),0)+1 from t_announce
INSERT INTO t_announce ( announc_id ,year ,seq ,contents ,act_month ,mng_stadt ,mng_enddt ,aply_stadt ,aply_enddt ,eval_stadt ,eval_enddt ,open_yn ,user_cd ,rel_dt ,reg_user_id ,reg_dt ,upd_user_id ,upd_dt ) VALUES ( #{announcId} ,#{year} ,nvl((select max(seq) from t_announce where year = #{year}),0)+1 ,#{contents} ,#{actMonth} ,REPLACE(#{mngStaDt},'-','') ,REPLACE(#{mngEndDt},'-','') ,REPLACE(#{aplyStaDt},'-','') ,REPLACE(#{aplyEndDt},'-','') ,REPLACE(#{evalStaDt},'-','') ,REPLACE(#{evalEndDt},'-','') ,#{openYn} ,#{userCd} ,REPLACE(#{relDt},'-','') ,#{*userId} ,TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') ,#{*userId} ,TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') )

mapper.update:
    /* kr.apfs.local.announce.dao.impl.AnnounceDao.updateAnnounce */
    UPDATE
 		t_announce
	SET
			contents = #{contents}
			,mng_stadt = REPLACE(#{mngStaDt},'-','')
			,mng_enddt =REPLACE(#{mngEndDt},'-','')
			,aply_stadt = REPLACE(#{aplyStaDt},'-','')
			,aply_enddt = REPLACE(#{aplyEndDt},'-','')
			,eval_stadt = REPLACE(#{evalStaDt},'-','')
			,eval_enddt = REPLACE(#{evalEndDt},'-','')
			,open_yn = #{openYn}
			,rel_dt = REPLACE(#{relDt},'-','')
			,upd_user_id = #{*userId}
			,upd_dt = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
	WHERE announc_id = #{announcId}

mapper.delete:
    /* kr.apfs.local.announce.dao.impl.AnnounceDao.deleteAnnounce */
 	UPDATE t_announce
 	SET del_yn = 'Y'
	WHERE announc_id = #{announcId}
